import React from 'react';
import styles from '../../src/Style.module.css';
import { useDrag } from "react-dnd";
//import { DndProvider} from "react-dnd";
//import { HTML5Backend } from "react-dnd-html5-backend";

//Väliaikainen komponentti, millä testailen DnD
const Laiva =() => { 

    const [{isDragging}, drag] = useDrag({
        item: {name: 'Any custom name', 
        type: 'Our first type'},
        end: (item, monitor) => {
            
            //Tähän luultavasti pitää laittaa se mitä tapahtuu kun tiputtaa hyväksyttävään soluun
            //Solu 1.1 on hyväksytty Field.js sisällä


            /*const dropResult = monitor.getDropResult();
            if(dropResult && dropResult.name === 'Column 1'){
                setIsFirstColumn(true)
            } else {
                setIsFirstColumn(false);
            }*/
        },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    });

    //const opacity = isDragging ? 0.4 : 1;

    return (
      
        <div ref={drag} className='movable-item'>
            <tr className={styles.td}>1</tr> 
        </div>   
    )
};


export default Laiva;
