import React from 'react';
import styles from '../../src/Style.module.css';
import { useDrag } from "react-dnd";
//import { DndProvider} from "react-dnd";
//import { HTML5Backend } from "react-dnd-html5-backend";

//Väliaikainen komponentti, millä testailen DnD
const Laiva =() => { 

    const [, drag] = useDrag({
        item: {name: 'laiva1', //Required. A plain JavaScript object describing the data being dragged. This is the only information available to the drop targets about the drag source so it's important to pick the minimal data they need to know. It's a good idea to return something like { type, id } from this method.
        type: 'laiva', //item.type must be set, and it must be either a string, an ES6 symbol. Only the drop targets registered for the same type will react to this item.
        id: 1}, //yksilöivä ID laivoille?
        end: (item, monitor) => {
            console.log("end");
            //Tähän luultavasti pitää laittaa se mitä tapahtuu kun tiputtaa hyväksyttävään soluun
            //Solu 1.1 on hyväksytty Field.js sisällä


            /*const dropResult = monitor.getDropResult();
            if(dropResult && dropResult.name === 'Column 1'){
                setIsFirstColumn(true)
            } else {
                setIsFirstColumn(false);
            }*/
        },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    });


    return (
      
        <div ref={drag} className='movable-item'>
            <tr>
                <td className={styles.td} >1</td>
            </tr>
        </div>   
    )
};


export default Laiva;
