import React from 'react';
import styles from '../../src/Style.module.css';
import { DndProvider, useDrag } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

//Väliaikainen komponentti, millä testailen DnD
const Laiva =() => { 

    const [{isDragging}, drag] = useDrag({
        item: {name: 'Any custom name', type: 'Our first type'},
        end: (item, monitor) => {
            const dropResult = monitor.getDropResult();
            if(dropResult && dropResult.name === 'Column 1'){
                setIsFirstColumn(true)
            } else {
                setIsFirstColumn(false);
            }
        },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    });

    const opacity = isDragging ? 0.4 : 1;

    return (
      
        <div ref={drag} className='movable-item' style={{opacity}}>
            <button type="button" className="btn btn-primary">Shiip</button> 
        </div>   
    )
};


export default Laiva;
